sqlMap.select:
SELECT * FROM ( SELECT ROWNUM rnum , A.* FROM ( SELECT 'R' flag /*조회상태*/ ,D.item_code /* 품목코드 */ ,D.pjt_id /* 프로젝트ID */ ,D.pjt_name /* 프로젝트이름 */ ,D.csr_id /* 업무요청ID */ ,D.csr_info_seq /* 업무요청순번 */ ,D.csr_title /* 요청제목 */ ,D.csr_type_code /*업무요청유형코드*/ ,( SELECT com_code_name FROM com_code WHERE group_code = 'SR_TYPE_CODE' AND com_code = D.CSR_TYPE_CODE AND use_yn ='Y' ) csr_type_name /*업무요청구분명*/ ,D.rowcnt /*rowspan 수*/ ,D.cnt /*제품별 시퀀스*/ ,D.pjt_date_from /*프로젝트기간 FROM*/ ,D.pjt_date_to /*프로젝트기간 TO*/ ,E.item_name /*품목명*/ FROM ( SELECT a.item_code ,ROW_NUMBER() OVER (PARTITION BY a.item_code order by a.item_code) cnt ,COUNT(*) OVER (PARTITION BY a.item_code order by a.item_code) rowcnt ,a.pjt_id ,a.csr_id ,a.csr_info_seq ,b.csr_title ,b.csr_type_code ,c.pjt_name ,c.pjt_date_from ,c.pjt_date_to FROM csr_pattern_item a, csr_info b, pjt_info c WHERE a.csr_status_code ='10' /*REQUEST_STATUS_CODE 10-수락*/ AND a.deal_yn ='10' /*SR_FLOW_CODE 10-요청중*/ AND a.csr_id = b.csr_id AND b.csr_type_code != 20 /*업무요청구분코드SR_TYPE_CODE: 개발-10, 지원-20, 기타-90*/ AND a.pjt_id = c.pjt_id(+) )D, com_item E WHERE D.item_code = E.item_code
AND D.pjt_name LIKE '%'||#src_pjt_name#||'%'
) A ) WHERE rnum > #start# AND rnum <= #limit#

SELECT COUNT(*) FROM ( SELECT 'R' flag /*조회상태*/ ,D.item_code /* 품목코드 */ ,D.pjt_id /* 프로젝트ID */ ,D.pjt_name /* 프로젝트이름 */ ,D.csr_id /* 업무요청ID */ ,D.csr_info_seq /* 업무요청순번 */ ,D.csr_title /* 요청제목 */ ,D.csr_type_code /*업무요청유형코드*/ ,( SELECT com_code_name FROM com_code WHERE group_code = 'SR_TYPE_CODE' AND com_code = D.CSR_TYPE_CODE AND use_yn ='Y' ) csr_type_name /*업무요청구분명*/ ,D.rowcnt /*rowspan 수*/ ,D.cnt /*제품별 시퀀스*/ ,D.pjt_date_from /*프로젝트기간 FROM*/ ,D.pjt_date_to /*프로젝트기간 TO*/ ,E.item_name /*품목명*/ FROM ( SELECT a.item_code ,ROW_NUMBER() OVER (PARTITION BY a.item_code order by a.item_code) cnt ,COUNT(*) OVER (PARTITION BY a.item_code order by a.item_code) rowcnt ,a.pjt_id ,a.csr_id ,a.csr_info_seq ,b.csr_title ,b.csr_type_code ,c.pjt_name ,c.pjt_date_from ,c.pjt_date_to FROM csr_pattern_item a, csr_info b, pjt_info c WHERE a.csr_status_code ='10' /*REQUEST_STATUS_CODE 10-수락*/ AND a.deal_yn ='10' /*SR_FLOW_CODE 10-요청중*/ AND a.csr_id = b.csr_id AND b.csr_type_code != 20 /*업무요청구분코드SR_TYPE_CODE: 개발-10, 지원-20, 기타-90*/ AND a.pjt_id = c.pjt_id(+) )D, com_item E WHERE D.item_code = E.item_code
AND D.pjt_name LIKE '%'||#src_pjt_name#||'%'
)
